    start_headers = {
        "User-Agent": UA[random.randint(0, len(UA) - 1)],
    }
    
    def parse(self, response):
        rs = json.loads(response.text)
        articles = rs.get('articles') or rs.get('recommendArticles')
        if not articles:
            print('have no articles')
            return
        if not isinstance(articles, list):
            return self.produce_debugitem(response, 'json error')
        
        for i in articles:
            # http://3g.k.sohu.com/t/n365358472?showType=   # 点击事件 展开全文
            # http://3g.k.sohu.com/t/n365358472?showType=0  # 全文文字视频都有，图片占位符有，但没有图片链接 不能用
            if 'newsItem' in i:
                news_item = i.get("newsItem")
                print(news_item)
                if not news_item:
                    print('have not newsItem')
                    continue
            else:
                news_item = deepcopy(i)
            newsId = news_item.get('newsId')
            if newsId < 1000:
                # print('newsid = %s, news_item=%s'%(newsId, news_item))
                continue
            news_url = 'http://3g.k.sohu.com/t/n{}?showType='.format(newsId)

            # if news_item.get('newsType') != 3:  # 新闻类型一般都为3   有4, 25, 53, 21, 85
            #     print('新闻类型!=3, =%s, newsid=%s' % (news_item.get('newsType'), newsId))
            
            if news_item.get('isHasTv') == 1:  # 排除视频
                continue
            title = i.get('title')
            subtitle = i.get("subTitle")
            if subtitle and subtitle != 'null':
                title += '——' + subtitle
            
            pubtime = news_item.get('time') or news_item.get('updateTime')  # 已验证time是新闻详情页的发布时间,  比updateTime早
            origin_name = i.get('media') or i.get('mediaName')
            
            yield Request(
                url=news_url,
                callback=self.parse_item,
                meta={'source_id': response.meta['source_id'], 'title': title, 'pubtime': pubtime,
                       'origin_name': origin_name, 'newsid': newsId,
                      'jstype': True,  # 新闻详情页用浏览器解决了状态码500的问题
                      'start_url_time': response.meta.get('start_url_time'), 'schedule_time': response.meta.get('schedule_time')}
            )
    
    def parse_item(self, response):
        page_title = self.get_page_title(response)
        if page_title and '404 Not Found' in page_title:
            print('url: %s, 404 Not Found' % response.url)
            return
        xp = response.xpath

        try:
            if "新闻已删除" in xp("//h1[@class='at-title']/text()").extract_first(''):
                return
            pubtime = response.request.meta['pubtime'] or xp("//span[@class='at-time']/text()").extract_first()
            cvs = xp('//div[@class="at-cnt-main"]') or xp('//div[@class="at-content ui-cf"]')
            content_div = cvs[0].extract()
            content, media, videos, video_cover = self.content_clean(content_div,
                                                                     kill_xpaths=['//*[text()="打开APP，查看更多精彩图片"]',
                                                                                  '//*[@id="at-img-all"]',
                                                                                  '//*[contains(text(),"展开全部")]',
                                                                                  '//*[contains(text(),"展开全文")]',
                                                                                  '//*[contains(text(), "打开搜狐新闻APP")]'
                                                                                  ])
        except:
            return self.produce_debugitem(response, "xpath error")

        return self.produce_item(
            response=response,
            title=response.request.meta['title'],
            pubtime=pubtime,
            origin_name=response.request.meta['origin_name'],
            content=content,
            media=media
        )
